package cap5;

/**
 * @author raphael
 *
 */
public class TesteOperadoresCompostos {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1 = 2;
		n1 *= 12; // n1=n1*12
		System.out.println(n1);
		
		n1 = 4;
		n1 += 4; // n1=n1+4
		System.out.println(n1);

		n1 = 9;
		n1 /= 3; // n1=n1/3
		System.out.println(n1);
		
		n1 = 5;
		n1 -= 5; // n1=n1-5
		System.out.println(n1);
	}

}
