public class TesteVariaveisLocais {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// variável local nome (String) inicializada com o valor Estudo Comunitario Java
		String nome = "Estudo Comunitario Java";
		
		// variável local f (tipo float) inicializada com o valor 23.5f
		float f = 23.5f;
		
		// variável local int i não inicializada
		int i;
		
		System.out.println("nome : " + nome);
		System.out.println("f : " + f);
		System.out.println("i : " + i);
	}

}
